import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme) => ({
  root: {},
  media: {
    height: 0,
    // paddingTop: "56.25%", // 16:9
    paddingTop: "100%", // 16:9
  },
}));

export default useStyles;
